mapper.select:
select id, enabled, name, pid, REVISION, CREATED_BY, CREATED_TIME, UPDATED_BY, UPDATED_TIME
from user_auth_menu
where id = #{id,jdbcType=INTEGER}

select id, enabled, name, pid from user_auth_menu where 1 = 1
and pid is null
and pid = #{id}

select id, enabled, name, pid, REVISION, CREATED_BY, CREATED_TIME, UPDATED_BY, UPDATED_TIME
from user_auth_menu
where name = #{name}
