mapper.select:
select id, name, enabled, REVISION, CREATED_BY, CREATED_TIME, UPDATED_BY, UPDATED_TIME from user_auth_module where 1=1
and name like concat('%', #{name}, '%')
and CREATED_BY like concat('%', #{createdBy}, '%')
and UPDATED_BY like concat('%', #{updatedBy}, '%')
and CREATED_TIME >= #{createdStartTime}
and CREATED_TIME <= #{createdEndTime}
